package com.example.chatapplication.services;

import com.example.chatapplication.domain.Account;
import com.example.chatapplication.domain.CaptureScreen;
import com.example.chatapplication.repositories.AccountRepository;
import com.example.chatapplication.repositories.CaptureScreenRepository;
import com.example.chatapplication.services.dto.CaptureScreenDto;
import com.example.chatapplication.services.impl.CaptureScreenServiceImpl;
import com.example.chatapplication.services.mapper.CaptureScreenMapper;
import com.example.chatapplication.ultities.SecurityUtils;
import org.junit.Assert;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.powermock.api.mockito.PowerMockito;
import org.powermock.core.classloader.annotations.PowerMockIgnore;
import org.powermock.core.classloader.annotations.PrepareForTest;
import org.powermock.modules.junit4.PowerMockRunner;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.data.domain.Sort;
import org.springframework.test.util.ReflectionTestUtils;

import javax.imageio.ImageIO;
import java.time.LocalDateTime;
import java.util.ArrayList;
import java.util.List;
import java.util.Optional;

import static org.mockito.Mockito.*;

@PowerMockIgnore({"javax.management.*", "javax.script.*"})
@RunWith(PowerMockRunner.class)
@PrepareForTest({SecurityUtils.class, ImageIO.class})
public class CaptureScreenServiceTest {

    @Mock
    private AccountRepository accountRepository;

    @Mock
    private CaptureScreenRepository captureScreenRepository;

    @Mock
    private CaptureScreenMapper captureScreenMapper;

    @InjectMocks
    private CaptureScreenService captureScreenService = new CaptureScreenServiceImpl();

    @Before
    public void init() {
        ReflectionTestUtils.setField(captureScreenService, // inject into this object
                "rootDir", // assign to this field
                "D:\\ChatApplication\\"); // object to be injected
    }


    @Test
    public void saveCapture() {
        String dataImg = "data:image/png;base64,";

        Optional<String> optionalUsername = Optional.of("emp001");

        PowerMockito.mockStatic(SecurityUtils.class);
        when(SecurityUtils.getAccountCurrentUserLogin()).thenReturn(optionalUsername);
        String username = SecurityUtils.getAccountCurrentUserLogin().get();

        Account account = new Account();
        account.setUsername(username);
        when(accountRepository.findByUsername(username)).thenReturn(account);

        CaptureScreen captureScreen = new CaptureScreen();
        captureScreen.setAccount(account);
        captureScreen.setCreatedBy(username);
        captureScreen.setUpdatedBy(username);
        when(captureScreenRepository.save(captureScreen)).thenReturn(captureScreen);

        captureScreenService.saveCapture(dataImg);
        //Because dataImg not enough data of image when convert base 64 => exception anh can't save data to db.
        verify(captureScreenRepository, times(0)).save(captureScreen);
    }

    @Test
    public void saveCapture2() {
        String dataImg = "data:image/png;base64,";

        Optional<String> optionalUsername = Optional.of("emp001");

        PowerMockito.mockStatic(SecurityUtils.class);
        when(SecurityUtils.getAccountCurrentUserLogin()).thenReturn(optionalUsername);
        String username = SecurityUtils.getAccountCurrentUserLogin().get();

        Account account = new Account();
        account.setUsername(username);
        when(accountRepository.findByUsername(username)).thenReturn(account);

        CaptureScreen captureScreen = new CaptureScreen();
        captureScreen.setAccount(account);
        captureScreen.setCreatedBy(username);
        captureScreen.setUpdatedBy(username);
        when(captureScreenRepository.save(captureScreen)).thenReturn(captureScreen);


        captureScreenService.saveCapture(dataImg);
        //Because dataImg not enough data of image when convert base 64 => exception anh can't save data to db.
        verify(captureScreenRepository, times(0)).save(captureScreen);
    }

    @Test
    public void findAllByAccount() {

        Sort sortCreatedDate = Sort.by(Sort.Direction.DESC, "createdDate");
        Pageable pageable = PageRequest.of(0, 50, sortCreatedDate);

        Account account = new Account();
        account.setUsername("emp001");
        when(captureScreenRepository.findAllByAccountOrderByCreatedDateDesc(account, pageable)).thenReturn(new ArrayList<>());


        CaptureScreen captureScreen = new CaptureScreen();
        CaptureScreenDto captureScreenDto = new CaptureScreenDto();
        when(captureScreenMapper.toDto(captureScreen)).thenReturn(captureScreenDto);

        Assert.assertEquals(0, captureScreenService.findAllByAccount(account, pageable).size());

    }

    @Test
    public void findCaptureById() {
        long idCapture = 1l;
        CaptureScreen captureScreen = new CaptureScreen();
        captureScreen.setId(idCapture);

        when(captureScreenRepository.findById(idCapture)).thenReturn(Optional.of(captureScreen));

        CaptureScreenDto captureScreenDto = new CaptureScreenDto();
        captureScreenDto.setId(idCapture);

        when(captureScreenMapper.toDto(captureScreen)).thenReturn(captureScreenDto);

        Assert.assertEquals(idCapture, captureScreenService.findCaptureById(idCapture).getId());
    }

    @Test
    public void findAllByAccountAndCreateDate() {

        Sort sortCreatedDate = Sort.by(Sort.Direction.DESC, "createdDate");
        Pageable pageable = PageRequest.of(0, 50, sortCreatedDate);

        Account account = new Account();
        account.setUsername("emp001");

        String dateStr = "2020-09-06T15:23:01";
        LocalDateTime time = LocalDateTime.parse(dateStr);

        CaptureScreen captureScreen = new CaptureScreen();
        List<CaptureScreen> captureScreens = new ArrayList<>();
        captureScreens.add(captureScreen);


        when(captureScreenRepository.findAllByAccountAndCreatedDateAfterOrderByCreatedDateDesc(account, time, pageable))
        .thenReturn(captureScreens);

        CaptureScreenDto captureScreenDto = new CaptureScreenDto();
        when(captureScreenMapper.toDto(captureScreen)).thenReturn(captureScreenDto);

        Assert.assertEquals(captureScreens.size(), captureScreenService.findAllByAccountAndCreateDate(account,dateStr,pageable).size());
    }
}